var
    mongoose        = require('mongoose'),
    Schema          = mongoose.Schema;

var userSchema      = mongoose.Schema({
  token: String,
  email: String,
  hashed_password: String,
  salt: String,
  temp_str: String
});

// connect with DB
mongoose.connect('mongodb://localhost:27017/messenger');
module.exports      = mongoose.model('users', userSchema);
